import React, { useState } from 'react';
import styled from 'styled-components';
import Modal from '@material-ui/core/Modal';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';

const ModalContent = styled.div`
  width: 30%;
  position: absolute;
  top: 50%;
  right: 50%;
  transform: translate(50%, 50%);
  background: white;
`;

// Create a form
// Connect to the server
// title, des, image

const CakeCollection = (props: any) => {
  const [showModal, setShowModal] = useState(false);

  const onToggleModal = (
    e: React.MouseEvent<HTMLButtonElement, MouseEvent>
  ): void => {
    e.preventDefault();
    setShowModal((show: boolean) => !show);
  };

  return (
    <section>
      <button onClick={onToggleModal}>Add a collection</button>
      <Modal open={showModal} onClose={onToggleModal}>
        <ModalContent>
          <h1>Add your favorite cake.</h1>
          <FormControl>
            <InputLabel htmlFor="my-input">Email address</InputLabel>
            {/* <Input id="my-input" aria-describedby="my-helper-text" /> */}
            {/* <FormHelperText id="my-helper-text">We'll never share your email.</FormHelperText> */}
          </FormControl>
        </ModalContent>
      </Modal>
    </section>
  );
};

export default CakeCollection;
