import React, { useState, useEffect, useCallback } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
  Link,
} from 'react-router-dom';
import axios from 'axios';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/styles';

import Signin from './pages/Signin';
import Signup from './pages/Signup';
import CakeCollection from './pages/CakeCollection';
import PageNotFound from './pages/PageNotFound';

const theme = createMuiTheme({
  typography: {
    fontFamily: ['Arial', '"Apple Color Emoji"'].join(','),
  },
});

// const PrivateRoute: React.FC<{
//   component: React.FC;
//   path: string;
//   exact: boolean;
//   isAuth: boolean;
// }> = props => {
//   const { path, exact, component, isAuth } = props;
//   if (isAuth) {
//     console.log('isAuth');
//     return <Route path={path} exact={exact} component={component} />;
//   } else {
//     console.log('is NOT Auth');
//     return <Redirect to="/signin" />;
//   }
// };

const PrivateRoute = (props: any) => {
  const { isAuth } = props;
  return isAuth ? (
    <Route
      path={props.path}
      exact={props.exact}
      component={props.component}
      {...props}
    />
  ) : (
    <Redirect to="/signin" />
  );
};

const App = () => {
  const [isAuth, setIsAuth] = useState(false);
  console.log({ isAuth });

  const handleCheckAuth = useCallback(async () => {
    const res = await axios('/jwt_get');
    const token = await res.data.token;
    if (token) {
      console.log({ token });
      setIsAuth(true);
    }
  }, []);

  useEffect(() => {
    handleCheckAuth();
  }, [handleCheckAuth]);

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Link to="/">Collection</Link>

        <Switch>
          <PrivateRoute
            path="/"
            exact={true}
            // isAuth={isAuth}
            // component={CakeCollection}
            component={() => <CakeCollection isAuth={isAuth} />}
          />
          <Route
            path="/signin"
            exact={true}
            component={Signin}
            // isAuth={isAuth}
          />
          <Route
            path="/signup"
            exact={true}
            component={() => <Signup onCheckAuth={handleCheckAuth} />}
          />
          <PrivateRoute
            path="/collection"
            exact={true}
            component={CakeCollection}
            isAuth={isAuth}
          />
          <Route component={PageNotFound} />
        </Switch>
      </Router>
    </ThemeProvider>
  );
};

export default App;
